input_x = tf.placeholder(tf.int32,[None,None])

output_y = tf.placeholder(tf.int32,[None,])


word_embedding = tf.get_variable('embeddin',shape=[len(vocab_),110],dtype=tf.float32,initializer=tf.random_uniform_initializer(-0.01,0.01))

embedding_look=tf.nn.embedding_lookup(word_embedding,input_x)

sequence_len=tf.count_nonzero(input_x,axis=-1)

with tf.variable_scope('encoder') as scope:
    cell=rnn.LSTMCell(num_units=123)
    model=tf.nn.bidirectional_dynamic_rnn(cell,cell,dtype=tf.float32,sequence_length=sequence_len,inputs=embedding_look)
model1,(fs,fc)=model
data_final=tf.concat((tf.transpose(model1[0],[1,0,2])[-1],tf.transpose(model1[1],[1,0,2])[-1]),axis=-1)

weightsa=tf.get_variable('weight',shape=[2*123,len(labels_x)],dtype=tf.float32,initializer=tf.random_uniform_initializer(-0.01,0.01))

biass = tf.get_variable('biass',shape=[len(labels_x),],dtype=tf.float32,initializer=tf.random_uniform_initializer(-0.01,0.01))

resultt=tf.matmul(data_final,weightsa)+biass

normalaizationt=tf.nn.softmax(resultt)
pred=tf.argmax(normalaizationt,axis=-1)

#cross
ce=tf.nn.sparse_softmax_cross_entropy_with_logits(logits=resultt,labels=labels_x)
losss=tf.reduce_mean(ce)

#evaluate
accuracye=tf.reduce_mean(tf.cast(tf.equal(tf.cast(pred,tf.int32),labels_x),tf.float32))

trainop=tf.train.AdamOptimizer().minimize(losss)

with tf.Session() as tt:
    tt.run(tf.global_variables_initializer())
    for i in range(10000):
        aa,bb,cc,dd,ee,ff,gg,hh,ii,jj=tt.run([word_embedding,embedding_look,model,data_final,resultt,normalaizationt,pred,losss,accuracye,trainop],feed_dict={input_x:data_pad})

        print("loss",hh)
        print("acc",ii)
    print(accuracye())
